) AS [Source] ([EMAIL_TEMPLATE_ID],[EMAIL_TEMPLATE_SUBJECT_EN],[EMAIL_TEMPLATE_SUBJECT_FR],[EMAIL_TEMPLATE_BODY_EN],[EMAIL_TEMPLATE_BODY_FR],[EFFECTIVE_DATE],[EXPIRY_DATE],[USER_CREATED],[USER_UPDATED],[DATE_CREATED],[DATE_UPDATED],[EMAIL_TEMPLATE_TYPE_ID])
ON ([Target].[EMAIL_TEMPLATE_ID] = [Source].[EMAIL_TEMPLATE_ID])
WHEN MATCHED AND (
	NULLIF([Source].[EMAIL_TEMPLATE_SUBJECT_EN], [Target].[EMAIL_TEMPLATE_SUBJECT_EN]) IS NOT NULL OR NULLIF([Target].[EMAIL_TEMPLATE_SUBJECT_EN], [Source].[EMAIL_TEMPLATE_SUBJECT_EN]) IS NOT NULL OR 
	NULLIF([Source].[EMAIL_TEMPLATE_SUBJECT_FR], [Target].[EMAIL_TEMPLATE_SUBJECT_FR]) IS NOT NULL OR NULLIF([Target].[EMAIL_TEMPLATE_SUBJECT_FR], [Source].[EMAIL_TEMPLATE_SUBJECT_FR]) IS NOT NULL OR 
	NULLIF([Source].[EMAIL_TEMPLATE_BODY_EN], [Target].[EMAIL_TEMPLATE_BODY_EN]) IS NOT NULL OR NULLIF([Target].[EMAIL_TEMPLATE_BODY_EN], [Source].[EMAIL_TEMPLATE_BODY_EN]) IS NOT NULL OR 
	NULLIF([Source].[EMAIL_TEMPLATE_BODY_FR], [Target].[EMAIL_TEMPLATE_BODY_FR]) IS NOT NULL OR NULLIF([Target].[EMAIL_TEMPLATE_BODY_FR], [Source].[EMAIL_TEMPLATE_BODY_FR]) IS NOT NULL OR 
	NULLIF([Source].[EFFECTIVE_DATE], [Target].[EFFECTIVE_DATE]) IS NOT NULL OR NULLIF([Target].[EFFECTIVE_DATE], [Source].[EFFECTIVE_DATE]) IS NOT NULL OR 
	NULLIF([Source].[EXPIRY_DATE], [Target].[EXPIRY_DATE]) IS NOT NULL OR NULLIF([Target].[EXPIRY_DATE], [Source].[EXPIRY_DATE]) IS NOT NULL OR 
	NULLIF([Source].[USER_CREATED], [Target].[USER_CREATED]) IS NOT NULL OR NULLIF([Target].[USER_CREATED], [Source].[USER_CREATED]) IS NOT NULL OR 
	NULLIF([Source].[USER_UPDATED], [Target].[USER_UPDATED]) IS NOT NULL OR NULLIF([Target].[USER_UPDATED], [Source].[USER_UPDATED]) IS NOT NULL OR 
	NULLIF([Source].[DATE_CREATED], [Target].[DATE_CREATED]) IS NOT NULL OR NULLIF([Target].[DATE_CREATED], [Source].[DATE_CREATED]) IS NOT NULL OR 
	NULLIF([Source].[DATE_UPDATED], [Target].[DATE_UPDATED]) IS NOT NULL OR NULLIF([Target].[DATE_UPDATED], [Source].[DATE_UPDATED]) IS NOT NULL OR 
	NULLIF([Source].[EMAIL_TEMPLATE_TYPE_ID], [Target].[EMAIL_TEMPLATE_TYPE_ID]) IS NOT NULL OR NULLIF([Target].[EMAIL_TEMPLATE_TYPE_ID], [Source].[EMAIL_TEMPLATE_TYPE_ID]) IS NOT NULL) THEN
 UPDATE SET
  [Target].[EMAIL_TEMPLATE_SUBJECT_EN] = [Source].[EMAIL_TEMPLATE_SUBJECT_EN], 
  [Target].[EMAIL_TEMPLATE_SUBJECT_FR] = [Source].[EMAIL_TEMPLATE_SUBJECT_FR], 
  [Target].[EMAIL_TEMPLATE_BODY_EN] = [Source].[EMAIL_TEMPLATE_BODY_EN], 
  [Target].[EMAIL_TEMPLATE_BODY_FR] = [Source].[EMAIL_TEMPLATE_BODY_FR], 
  [Target].[EFFECTIVE_DATE] = [Source].[EFFECTIVE_DATE], 
  [Target].[EXPIRY_DATE] = [Source].[EXPIRY_DATE], 
  [Target].[USER_CREATED] = [Source].[USER_CREATED], 
  [Target].[USER_UPDATED] = [Source].[USER_UPDATED], 
  [Target].[DATE_CREATED] = [Source].[DATE_CREATED], 
  [Target].[DATE_UPDATED] = [Source].[DATE_UPDATED], 
